import React, { useState } from "react";
import classes from "./authen.module.css";
import { useSelector, useDispatch } from "react-redux";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../navbar/Navbar";

const Login = () => {
  const state = useSelector((state) => state.login);
  const enteredEmail = useRef();
  const enteredPassWord = useRef();
  const [result, setResult] = useState();
  const navigate = useNavigate();

  const submitHandler = (e) => {
    e.preventDefault();
    const user = {
      enteredEmail: enteredEmail.current.value,
      enteredPassWord: enteredPassWord.current.value,
      isSignUp: state.isSignUp,
    };

    fetch("http://localhost:5000/signup", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    })
      .then((res) => res.json())
      .then((result) => {
        return result;
      });
    if (result.status === 404) {
      alert(`Register is not success: ${result.message}`);
    } else {
      navigate("/login");
    }
  };
  return (
    <>
      <Navbar />
      <div className={classes.main}>
        <h1>Sign Up</h1>
        <form action="/login" method="post" onSubmit={submitHandler}>
          <input
            className={classes.input_field}
            type="email"
            required
            placeholder="Enter Email"
            ref={enteredEmail}
            name="email"
          ></input>
          <input
            className={classes.input_field}
            type="password"
            required
            placeholder="Enter Password"
            ref={enteredPassWord}
            name="password"
          ></input>
          <button className={classes.button} type="submit">
            Create Account
          </button>
        </form>
      </div>
    </>
  );
};

export default Login;
