import React from "react";
import classes from "./authen.module.css";
import { useSelector, useDispatch } from "react-redux";
import { loginAction } from "../../store/login-slice";
import { useRef } from "react";

const AuthenForm = () => {
  const state = useSelector((state) => state.login);
  const dispatch = useDispatch();
  const enteredEmail = useRef();
  const enteredPassWord = useRef();
  const enteredUserName = useRef();
  const enteredFullName = useRef();
  const enteredPhone = useRef();
  const submitHandler = (e) => {
    // onSubmit();
    e.preventDefault();
    dispatch(loginAction.login(state));
    let user = {};
    if (state.isSignUp) {
      user = {
        enteredEmail: enteredEmail.current.value,
        enteredPassWord: enteredPassWord.current.value,
        enteredFullName: enteredFullName.current.value,
        enteredPhone: enteredPhone.current.value,
        enteredUserName: enteredUserName.current.value,
        isSignUp: state.isSignUp,
      };
    } else {
      user = {
        enteredEmail: enteredEmail.current.value,
        enteredPassWord: enteredPassWord.current.value,
        isSignUp: state.isSignUp,
      };
    }
    console.log(user);
    fetch("http://localhost:5000/user", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    });
  };
  return (
    <div className={classes.main}>
      <h1>{state.isSignUp === false ? "Login" : "Sign Up"}</h1>
      <form action="/" method="post" onSubmit={submitHandler}>
        <input
          className={classes.input_field}
          type="email"
          required
          placeholder="Enter Email"
          ref={enteredEmail}
          name="email"
        ></input>
        <input
          className={classes.input_field}
          type="password"
          required
          placeholder="Enter Password"
          ref={enteredPassWord}
          name="password"
        ></input>

        {state.isSignUp && (
          <>
            <input
              className={classes.input_field}
              type="text"
              required
              placeholder="Enter User Name"
              ref={enteredUserName}
              name="userName"
            ></input>
            <input
              className={classes.input_field}
              type="text"
              required
              placeholder="Enter Full Name"
              ref={enteredFullName}
              name="fullName"
            ></input>
            <input
              className={classes.input_field}
              type="tel"
              required
              placeholder="Enter Phone Number"
              ref={enteredPhone}
              name="phone"
            ></input>
          </>
        )}

        <button className={classes.button} type="submit">
          {state.isSignUp === false ? "Login" : "Create Account"}
        </button>
      </form>
    </div>
  );
};

export default AuthenForm;
