import React, { useState, useRef } from "react";
import classes from "./BookingPage.module.css";
import { DateRange } from "react-date-range";

const BookingPage = ({ roomsList }) => {
  const fullNameRef = useRef();
  const emailRef = useRef();
  const phoneRef = useRef();
  const idNumberRef = useRef();
  const fullName = fullNameRef.current.value;
  const email = emailRef.current.value;
  const phone = phoneRef.current.value;
  const idNumber = idNumberRef.current.value;

  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  console.log(date);

  // const hiringDate =
  //   new Date(date.endDate).getTime() - new Date(date.startDate).getTime();

  // const day = hiringDate / 1000 / 86400;

  const handleClick = () => {
    console.log(date);
  };

  return (
    <div className={classes.main}>
      <form className={classes.header}>
        <div className={classes.left}>
          <h1>Date</h1>
          <DateRange
            editableDateInputs={true}
            onChange={(item) => setDate([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={date}
            minDate={new Date()}
          />
        </div>
        <div className={classes.right}>
          <h1>Reverse Info</h1>
          <div>
            <label htmlFor="fullName">Your Full Name</label>
            <input
              id="fullName"
              type="text"
              required
              placeholder="Full Name"
              ref={fullNameRef}
            ></input>

            <label htmlFor="email">Your Email</label>

            <input
              id="email"
              type="email"
              required
              placeholder="Email"
              ref={emailRef}
            ></input>

            <label htmlFor="tel">Your Phone Number</label>
            <input
              id="tel"
              type="tel"
              required
              placeholder="Phone Number"
              ref={phoneRef}
            ></input>

            <label htmlFor="idNumber">Your Identy Card Number</label>
            <input
              id="idNumber"
              type="text"
              required
              placeholder="Identify Number"
              ref={idNumberRef}
            ></input>
          </div>
        </div>
      </form>

      <div className={classes.footer}>
        <div className={classes.room}>
          <h1>Select Room</h1>
          {roomsList.map((room) => {
            return (
              <div className={classes.allRoom}>
                <div className={classes.room_list}>
                  <div className={classes.room_item}>
                    <h4>{room.title}</h4>
                    <p>{room.desc}</p>
                    <p>Max People: {room.maxPeople}</p>
                    <b>${room.price}</b>
                  </div>
                  <div className={classes.checkbox}>
                    {room.roomNumbers.map((roomNumber) => {
                      return (
                        <form>
                          <label htmlFor={`${room._id}-${roomNumber}`}>
                            {roomNumber}
                          </label>
                          <input
                            type="checkbox"
                            id={`${room._id}-${roomNumber}`}
                          ></input>
                        </form>
                      );
                    })}
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className={classes.checkout}>
          <div className={classes.payment}>
            <h1>Total Bill: ${day}</h1>
            <select>
              <option>Select Payment Method</option>
              <option>Credit Card</option>
              <option>Cash</option>
            </select>
          </div>
          <button className={classes.btn} onClick={handleClick}>
            Reserve Now!
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
