import React, { useState, useRef, useEffect } from "react";
import classes from "./BookingPage.module.css";
import { DateRange } from "react-date-range";
import { useLocation } from "react-router-dom";

const BookingPage = () => {
  const [rooms, setRooms] = useState([]);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const location = useLocation();
  const hotelId = location.pathname.split("/")[2];
  console.log(hotelId);
  useEffect(() => {
    async function fetchDetail(id) {
      const response = await fetch(`http://localhost:5000/hotels/${id}`);
      const data = await response.json();
      const allRoomId = await data.rooms;

      const allRooms = Promise.all(
        allRoomId.map(async (roomId) => {
          const response = await fetch(`http://localhost:5000/rooms/${roomId}`);
          const data = await response.json();
          return data;
        })
      );
      console.log(allRooms);
    }
    fetchDetail(hotelId);
  }, [hotelId]);

  return (
    <div className={classes.main}>
      <form className={classes.header}>
        <div className={classes.left}>
          <h1>Date</h1>
          <DateRange
            editableDateInputs={true}
            onChange={(item) => setDate([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={date}
            minDate={new Date()}
          />
        </div>
        <div className={classes.right}>
          <h1>Reverse Info</h1>
          <div>
            <label htmlFor="fullName">Your Full Name</label>
            <input
              id="fullName"
              type="text"
              required
              placeholder="Full Name"
            ></input>

            <label htmlFor="email">Your Email</label>
            <input id="email" type="email" required placeholder="Email"></input>

            <label htmlFor="tel">Your Phone Number</label>
            <input
              id="tel"
              type="tel"
              required
              placeholder="Phone Number"
            ></input>

            <label htmlFor="idNumber">Your Identy Card Number</label>
            <input
              id="idNumber"
              type="text"
              required
              placeholder="Identify Number"
            ></input>
          </div>
        </div>
      </form>

      <div className={classes.footer}>
        <div className={classes.room}>
          <h1>Select Room</h1>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
