import "./navbar.css";
import { useReducer } from "react";
const initialState = { isLogin: false };

const reducer = (state, action) => {
  if (action.type === "Sign Up") {
    state.isLogin = true;
  }
  if (action.type === "Login") {
    state.isLogin = false;
  }
};

const Navbar = (props) => {
  const [login, dispatch] = useReducer(reducer, initialState);

  const signUpHandler = () => {
    dispatch({ type: "Sign Up" });
  };

  const loginHandler = () => {
    dispatch({ type: "Login" });
  };

  return (
    <>
      {login && (
        <div className="navbar">
          <div className="navContainer">
            <span className="logo">Booking Website</span>
            <div className="navItems">
              <button className="navButton" onClick={signUpHandler}>
                Sign Up
              </button>
              <button className="navButton" onClick={loginHandler}>
                Login
              </button>
            </div>
          </div>
        </div>
      )}

      {!login && (
        <div className="navbar">
          <div className="navContainer">
            <span className="logo">Booking Website</span>
            <div className="navItems">
              <p>Email</p>
              <button className="navButton">Transacsions</button>
              <button className="navButton">Logout</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
